use std::io::Read;

fn read_version(transaction_bytes: &mut &[u8]) -> u32 {
    let mut buffer = [0; 4];
    transaction_bytes.read(&mut buffer).unwrap();

    u32::from_le_bytes(buffer)
}

fn read_compact_size(transaction_bytes: &mut &[u8]) -> u64 {
    let mut compact_size = [0; 1];
    transaction_bytes.read(&mut compact_size).unwrap();

    // using a ref prevents making a copy, i would think
    // and deref trait lets it be used for pattern matching
    let first_byte = compact_size[0];
    match first_byte {
        (1..=252) => compact_size[0] as u64,
        253 => {
            let mut buffer = [0; 2];
            transaction_bytes.read(&mut buffer).unwrap();
            let extra_bytes = u16::from_le_bytes(buffer);
            first_byte as u64 + extra_bytes as u64
        }
        254 => {
            let mut buffer = [0; 4];
            transaction_bytes.read(&mut buffer).unwrap();
            let extra_bytes = u32::from_le_bytes(buffer);
            first_byte as u64 + extra_bytes as u64
        }
        255 => {
            let mut buffer = [0; 8];
            transaction_bytes.read(&mut buffer).unwrap();
            let extra_bytes = u64::from_le_bytes(buffer);
            first_byte as u64 + extra_bytes
        }
        _ => panic!("invalid compact size"),
    }
}
fn main() {
    let transaction_hex = "";
    let transaction_bytes = hex::decode(transaction_hex).unwrap();
    let mut bytes_slice = transaction_bytes.as_slice();

    let version = read_version(&mut bytes_slice);
    let input_length = read_compact_size(&mut bytes_slice);

    println!("Version: {}", version);
    println!("Input Length: {}", input_length);
}
